# Time limit exceeded

def countSubarrays(nums: list[int], minK: int, maxK: int) -> int:
    index = 0
    counter_subarr = []
    count = 0

    while index < len(nums):
        subarr_len = index + 1

        while subarr_len <= len(nums):
            subarr = []
            for i in range(index, subarr_len):
                subarr.append(nums[i])
            if max(subarr) == maxK and min(subarr) == minK:
                counter_subarr.append(subarr)
                count += 1
            subarr_len += 1

        index += 1
    print(counter_subarr)
    return count


